	<view class="teacher_detail">
		<tt-nav-bar
		left-icon="&#xe65a;" 
		right-icon="&#xe609;"
		:backgroundColor="backgroundColor"
		:title="title"
		@clickLeft="back"
		@clickRight="more"
		>
		</tt-nav-bar>
		<view class="base_info">
			<view class="content">
				<view class="text">
					<view class="">{{teacher.nickName}}</view>
				</view>
				<view class="pic">
					<view class="avatar">
						<image :src="teacher.avatarUrl" @click="preAvatar" mode="widthFix"></image>
					</view>
					<view class="icons text uni-flex">
						<text class="icon">&#xe63e;</text>
						<text class="icon">&#xe657;</text>
						<text class="icon">&#xe636;</text>
					</view>
				</view>
			</view>
		</view>
		<view class="signature">
			<text>{{teacher.signature}}</text>
		</view>
		<view class="credentials">
			<view class="title">
				<text>个人资历</text>
			</view>
			<view class="content">
				<view class="list">
					<text class="t1">家教经验</text>
					<text class="t2">{{teacher.experienceTime}}</text>
				</view>
				<view class="line"></view>
				<view class="list">
					<text class="t1">就读学校</text>
					<text class="t2">{{teacher.school}}</text>
				</view>
				<view class="line"></view>
				<view class="list">
					<text class="t1">职业</text>
					<text class="t2">{{teacher.job}}</text>
				</view>
				<view class="line"></view>
				<view class="list">
					<text class="t1">擅长科目</text>
					<text class="t2">{{teacher.course}}</text>
				</view>
			</view>
		</view>
		<view class="credentials" v-if="teacher.photo.length">
			<view class="title">
				<text>个人相册</text>
			</view>
			<view class="uni-uploader-body">
				<view class="uni-uploader__files">
					<block>
						<view class="uni-uploader__file" v-for="(item,index)  in teacher.photo" :key="index">
							<image class="uni-uploader__img" :src="item" @tap="previewImage"></image>
						</view>
					</block>
				</view>
			</view>
		</view>
		<view class="credentials">
			<view class="title">
				<text>教学经验</text>
			</view>
			<view class="content">
