			</view>
		</view>
		<view class="footer"></view>
	</view>
</template>

<script>
	import { mapState } from 'vuex'
	import ttNavBar from '@/components/tt-nav-bar/tt-nav-bar.vue'
	export default {
		components:{ttNavBar},
		data() {
			return {
				imageList:[
					'../../static/home/2.jpg'
				],
				backgroundColor:'transparent',
				title: '',
				url: '',
			}
		},
		computed: {
			...mapState({
				teacher: state => state.user.userDetail
			})
		},
		methods: {
			previewImage(e) {
				var current = e.target.dataset.src
				uni.previewImage({
					current: current,
					urls: this.teacher.photo
				})
			},
			preAvatar(){
				let current =this.teacher.avatarUrl
				let urls =[]
				urls.push(current)
				uni.previewImage({
					current,
					urls
				})
			},
			back(){
				uni.navigateBack()
			},
			more(){
				uni.showToast({
				    title: '点击更多',
					icon: 'none',
					position: 'bottom',
				    duration: 600
				});
			}
		},
		onPageScroll(option) {
			if(option.scrollTop > 5){
				this.backgroundColor = 'rgba(0,0,0,0.5)'
				this.title = this.teacher.nickName
			}
			else{
				this.backgroundColor = 'transparent'
				this.title = ''
			}
		}
	}
</script>

